import React, { useState, useEffect } from 'react';
import Axios from 'axios';
// import useAxios from 'axios-hooks'
import { Card, Col, Row, Badge, Descriptions, Tag, Tabs} from 'antd';
import { MoneyCollectOutlined, DollarOutlined } from '@ant-design/icons';
import { FcNumericalSorting12 } from "react-icons/fc";
import './Ranking.scss';
import FirstRank from './tab/FirstRank';
import AveRank from './tab/AveRank';

const Ranking = ({uniq_num}) => {
    const { TabPane } = Tabs;
    const firstRankUrl = `http://localhost:8000/api/first_rank/${uniq_num}`

    const [rank, setRank] = useState({})
    const [rankInfo, setRankInfo] = useState({})

    // 기준 선택 상태
    const [companyInfo, setCompanyInfo] = useState("전체")
    const [companyNumber, setCompanyNumber] = useState(0)
    const [all, setAll] = useState("processing")
    const [location, setLocation] = useState("default")
    const [superCompany, setSuperCompany] = useState("default")
    const [category, setCategory] = useState("default")

    useEffect(() => {
        Axios.get(firstRankUrl)
            .then(response => {
                const {data} = response;
                setRank({"all" : data.all, "location" : data.location, "authority" : data.authority, 'type' : data.type})
                setRankInfo(data.all)
                setCompanyNumber(data.all.company_number)

            })
    }, [firstRankUrl])




    const changeAll = () =>{
        if(all === "default"){
            setAll("processing")
            setLocation("default")
            setSuperCompany("default")
            setCategory("default")

            setRankInfo(rank.all)

            setCompanyInfo(rank.all.company_info)
            setCompanyNumber(rank.all.company_number)
        }
    }
    const changeLocation = () =>{
        if(location === "default"){
            setAll("default")
            setLocation("processing")
            setSuperCompany("default")
            setCategory("default")

            setRankInfo(rank.location)

            setCompanyInfo(rank.location.company_info)
            setCompanyNumber(rank.location.company_number)
        }
    }
    const changeSuperCompany = () =>{
        if(superCompany === "default"){
            setAll("default")
            setLocation("default")
            setSuperCompany("processing")
            setCategory("default")

            setRankInfo(rank.authority)

            setCompanyInfo(rank.authority.company_info)
            setCompanyNumber(rank.authority.company_number)
        }
    }

    const changeCategory = () =>{
        if(category === "default"){
            setAll("default")
            setLocation("default")
            setSuperCompany("default")
            setCategory("processing")

            setRankInfo(rank.type)

            setCompanyInfo(rank.type.company_info)
            setCompanyNumber(rank.type.company_number)
        }
    }

    function moneyToUnit(money) {
        return money.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ',');
    }


    return (
        <Row className="card_section">
            <Col xs={{span: 24}} sm={{ span: 14, offset: 5 }}>
                <Card  
                    title={
                        <span>
                            <div className='title_logo'><FcNumericalSorting12/></div> 
                            <div className='title_content'>순위</div>
                        </span>
                    } 
                    style={{ width: "100%" }}
                >
                <Tabs defaultActiveKey="1">

                    <TabPane
                    tab={
                        <span>
                        <MoneyCollectOutlined />
                        신입 초봉
                        </span>
                    }
                    key="1"
                    >

                    <FirstRank/>

                    </TabPane>

                    <TabPane
                    tab={
                        <span>
                        <DollarOutlined />
                        평균 연봉
                        </span>
                    }
                    key="2"
                    >

                    {/* <AveRank/> */}

                    </TabPane>
                </Tabs>
            </Card>
            </Col>
        </Row>
    );
};

export default Ranking;
