)


  return (
    <JwtContext.Provider value={jwtToken}>
    <Switch>
      <Route path='/rank/:uniq_num' component={RankPage}/>

      <Route path='/accounts/sign_in' component={SignInPage}/>
      <Route path='/accounts/sign_up' component={SignUpPage}/>

      <Route path='/' component={IndexPage}/>
    </Switch>
    </JwtContext.Provider>
  );
};

export {JwtContext};
export default App;
