import React, { useEffect, useState } from 'react';
// import useAxios from 'axios-hooks'
import Axios from 'axios';
import { Card, Col, Row, Table, Tabs, Badge} from 'antd';
import { MoneyCollectOutlined, DollarOutlined } from '@ant-design/icons';
import { FcTodoList } from "react-icons/fc";
import './TableList.scss';





const TableList = ({uniq_num}) => {
    const { TabPane } = Tabs;

    const rankColumn = [
      { title: '순위', dataIndex: 'rank', key: '1' },
    ]

    // const rankData = [
    //   {
    //       key: '1',
    //       rank: '1'
    //   },
    //   {
    //     key: '2',
    //     rank: '2'
    //   }
    // ]

    const columns = [
        //  { title: '순위', width: 60, dataIndex: 'rank', key: '1' },
         { title: '기업', width: 215, dataIndex: 'name', key: '2'},
         { title: '기본급', dataIndex: 'nomal', key: '3', sorter: {compare: (a, b) => a.nomal - b.nomal} },
         { title: '고정수당', dataIndex: 'fixed', key: '4', sorter: {compare: (a, b) => a.fixed - b.fixed} },
         { title: '실적수당', dataIndex: 'performance', key: '5', sorter: {compare: (a, b) => a.performance - b.performance} },
         { title: '복리후생비', dataIndex: 'benefit', key: '6', sorter: {compare: (a, b) => a.benefit - b.benefit} },
         { title: '성과 상여금', dataIndex: 'person_bonus', key: '7', sorter: {compare: (a, b) => a.person_bonus - b.person_bonus} },
         { title: '경평 성과급', dataIndex: 'company_bonus', key: '8', sorter: {compare: (a, b) => a.company_bonus - b.company_bonus} },
         { title: '기타', dataIndex: 'etc', key: '9', sorter: {compare: (a, b) => a.etc - b.etc}},
         {
            title: '총 연봉',
            key: 'operation',
            fixed: 'right',
            width: 100,
            dataIndex: 'total',
            sorter: {compare: (a, b) => a.total - b.total,multiple: 1},
          },
      ];

      // const [{ data, loading, error }] = useAxios(
      //   'http://localhost:8000/api/first_money/'
      // )

    const [all, setAll] = useState("processing")
    const [location, setLocation] = useState("default")
    const [superCompany, setSuperCompany] = useState("default")
    const [category, setCategory] = useState("default")

    const [status, setStatus] = useState("all")
    const [allMoney, setAllMoney] = useState()

    const [url, setUrl] = useState(`http://localhost:8000/api/first_money/${uniq_num}/all`)

    useEffect(() => {
        Axios.get(url)
            .then(response => {
                const {data} = response;
                setAllMoney(data);
            })      
    }, [url])

      const rankData = []
      allMoney && allMoney.forEach((data, index) => {
        let temp = {}

        temp['key'] = index + 1
        temp['rank'] = index + 1

        rankData.push(temp)
      });


      const changeAll = () =>{
        if(all === "default"){
            setAll("processing")
            setLocation("default")
            setSuperCompany("default")
            setCategory("default")

            setUrl(`http://localhost:8000/api/first_money/${uniq_num}/all`)
        }
      }
      const changeLocation = () =>{
          if(location === "default"){
              setAll("default")
              setLocation("processing")
              setSuperCompany("default")
              setCategory("default")

              setUrl(`http://localhost:8000/api/first_money/${uniq_num}/location`)
          }
      }
      const changeSuperCompany = () =>{
          if(superCompany === "default"){
              setAll("default")
              setLocation("default")
              setSuperCompany("processing")
              setCategory("default")

              setUrl(`http://localhost:8000/api/first_money/${uniq_num}/authority`)
          }
      }

      const changeCategory = () =>{
          if(category === "default"){
              setAll("default")
              setLocation("default")
              setSuperCompany("default")
              setCategory("processing")

              setUrl(`http://localhost:8000/api/first_money/${uniq_num}/location`)
          }
      }  





    return (
        <Row className="card_section">
            <Col xs={{span: 24}} sm={{ span: 14, offset: 5 }}>
                <Card 
                  title={
                    <span>
                        <div className='title_logo'><FcTodoList/></div> 
                        <div className='title_content'>전체</div>
                    </span>
                  } 
                  style={{ width: "100%" }}
                >
                <Tabs defaultActiveKey="1">
                  <TabPane
                    tab={
                      <span>
                        <MoneyCollectOutlined />
                        신입 초봉
                      </span>
                    }
                    key="1"
                  >
                    <Row>
                        <Col span={24}>
                            <div className="choice">
                                <Badge status={all} onClick={changeAll} text="전체"/>
                                <Badge status={location} onClick={changeLocation} text="소재지"/>
                                {/* <Badge status={superCompany} onClick={changeSuperCompany} text="주무부처"/>
                                <Badge status={category} onClick={changeCategory} text="기관유형"/> */}
                            </div>
                        </Col>
                    </Row>

                    <Row className="table_wrapper">
                      <Col xs={{span: 5}} sm={{ span: 2 }}>
                       <Table 
                        bordered={false}
                        columns={rankColumn} 
                        dataSource={rankData} 
                        pagination={{hideOnSinglePage: true}} 
                        />
                      </Col>
                      <Col xs={{span: 19}} sm={{ span: 22}}>
                        <Table 
                        columns={columns} 
                        dataSource={allMoney} 
                        scroll={{ x: 1200 }}
                        pagination={{hideOnSinglePage: true}} 
                        />
                      </Col>
                    </Row>

                  </TabPane>
                  <TabPane
                    tab={
                      <span>
                        <DollarOutlined />
                        평균 연봉
                      </span>
                    }
                    key="2"
                  >
                    Tab 2
                  </TabPane>
                </Tabs>    


                </Card>
            </Col>
        </Row>
    );
};

export default TableList;
