import React, { useState } from 'react';
import { Row, Col, Layout } from 'antd';
import { MenuOutlined } from '@ant-design/icons';
import './AppLayOut.scss';
import { Link,useLocation } from 'react-router-dom';
import {JwtContext} from '../../App';

const AppLayOut = ({children}) => {

    const { Header, Footer, Content } = Layout;
    const location = useLocation();

    const jwtToken = React.useContext(JwtContext)
    const [loginCheck, setLoginCheck] = useState(false);

    if(jwtToken.length > 0){
        setLoginCheck(true)
    }else{
        setLoginCheck(false)
    }

    return (
        <Layout>
            <Header className='header'>
                <Row>
                    <Col span={2}></Col>
                    <Col span={10}>logo</Col>
                    <Col span={10} className='user-button' style = {{textAlign: 'right'}}>
                        {location.pathname === '/accounts/sign_in' &&  
                            <Link to="/accounts/sign_up">
                                <span>회원가입</span>
                            </Link>
                        }

                        {location.pathname === '/accounts/sign_up' &&  
                            <Link to="/accounts/sign_in">
                                <span>로그인</span>
                            </Link>
                        }
                        {(location.pathname !== '/accounts/sign_up' && location.pathname !== '/accounts/sign_in') &&  
                            <div>
                                <Link to="/accounts/sign_in">
                                    <span>로그인</span>
                                </Link>
                                <Link to="/accounts/sign_in">
                                    <span>로그인</span>
                                </Link>
                            </div>
                        }
                    </Col>
                    <Col span={2}></Col>
                </Row>
            </Header>

            <Content className='main'>
                {children}
            </Content>

            <Footer className='footer'>
                <Row className='footer-section'>
                    <Col span={24}>
                        <p>위 사이트는 공공기관 경영정보 공개시스템 '알리오'에 공시된 자료를 기반으로 만들어졌습니다.</p>
                        <p>© Octopus</p>
                    </Col>
                </Row>
            </Footer>
        </Layout>
    );
};

export default AppLayOut;
