import React, { useState, useEffect } from 'react';
import Axios from 'axios';
// import useAxios from 'axios-hooks'
import { Card, Col, Row, Badge, Descriptions, Tag, Tabs} from 'antd';
import { MoneyCollectOutlined, DollarOutlined } from '@ant-design/icons';
import { FcNumericalSorting12 } from "react-icons/fc";
import './Ranking.scss';

const Ranking = ({uniq_num}) => {
    const { TabPane } = Tabs;
    const firstRankUrl = `http://localhost:8000/api/first_rank/${uniq_num}`

    const [rank, setRank] = useState({})
    const [rankInfo, setRankInfo] = useState({})

    // 기준 선택 상태
    const [all, setAll] = useState("processing")
    const [location, setLocation] = useState("default")
    const [superCompany, setSuperCompany] = useState("default")
    const [category, setCategory] = useState("default")

    useEffect(() => {
        Axios.get(firstRankUrl)
            .then(response => {
                const {data} = response;
                setRank({"all" : data.all, "location" : data.location, "authority" : data.authority, 'type' : data.type})
                console.log(rank)
                // setRankInfo(data.all)
            })
    }, [firstRankUrl])


    // const [{ data, loading, error }, refetch] = useAxios(
    //     firstRankUrl
    // )

    // console.log(data.all)
    // if(data){setRankInfo(data.all)}
    // console.log()

    const changeAll = () =>{
        if(all === "default"){
            setAll("processing")
            setLocation("default")
            setSuperCompany("default")
            setCategory("default")

            setRankInfo(rank.all)
        }
    }
    const changeLocation = () =>{
        if(location === "default"){
            setAll("default")
            setLocation("processing")
            setSuperCompany("default")
            setCategory("default")

            setRankInfo(rank.location)
        }
    }
    const changeSuperCompany = () =>{
        if(superCompany === "default"){
            setAll("default")
            setLocation("default")
            setSuperCompany("processing")
            setCategory("default")

            setRankInfo(rank.authority)
        }
    }

    const changeCategory = () =>{
        if(category === "default"){
            setAll("default")
            setLocation("default")
            setSuperCompany("default")
            setCategory("processing")

            setRankInfo(rank.type)
        }
    }


    return (
        <Row className="card_section">
            <Col xs={{span: 24}} sm={{ span: 14, offset: 5 }}>
                <Card  
                    title={
                        <span>
                            <div className='title_logo'><FcNumericalSorting12/></div> 
                            <div className='title_content'>순위</div>
                        </span>
                    } 
                    style={{ width: "100%" }}
                >
                <Tabs defaultActiveKey="1">

                    <TabPane
                    tab={
                        <span>
                        <MoneyCollectOutlined />
                        신입 초봉
                        </span>
                    }
                    key="1"
                    >
                    <Row>
                        <Col span={24}>
                            <div className="choice">
                                <Badge status={all} onClick={changeAll} text="전체"/>
                                <Badge status={location} onClick={changeLocation} text="소재지"/>
                                <Badge status={superCompany} onClick={changeSuperCompany} text="주무부처"/>
                                <Badge status={category} onClick={changeCategory} text="기관유형"/>
                            </div>
                        </Col>
                    </Row>

                    <Row>
                        <Col className="ranking" span={24}>
                            <div className='rank_wrapper'>
                                <span>{rankInfo.rank}</span> <span>등</span>
                            </div>
                            <div className='tag_wrapper'>
                                <Tag color="#108ee9">전체 공공기관</Tag>
                                <Tag color="#2db7f5">103개</Tag>
                                <Tag color="#87d068">상위 36%</Tag>
                            </div>
                        </Col>

                        <Col  className="money_summary" xs={{span: 24}} sm={{ span: 14, offset: 5 }}>
                            <Descriptions 
                                bordered  
                                layout="vertical"
                                column={{ xxl: 8, xl: 4, lg: 4, md: 4, sm: 4, xs: 4 }}
                            >
                                <Descriptions.Item label="최고" span={2}>
                                    <div className="money_section">{rankInfo.top && rankInfo.top.money}</div>
                                    <div className="company_section">({rankInfo.top && rankInfo.top.name})</div>
                                </Descriptions.Item>
                                <Descriptions.Item label="최저" span={2}>
                                    <div className="money_section">{rankInfo.bottom && rankInfo.bottom.money}</div> 
                                    <div className="company_section">({rankInfo.bottom && rankInfo.bottom.name})</div>
                                </Descriptions.Item>
                                <Descriptions.Item label="평균" span={2}>
                                    <div className="money_section">{rankInfo.middle && rankInfo.middle.money }</div>
                                </Descriptions.Item>
                                <Descriptions.Item label="중위값" span={2}>
                                    <div className="money_section">{rankInfo.center && rankInfo.center.money}</div>
                                    <div className="company_section">({rankInfo.center && rankInfo.center.name})</div>
                                </Descriptions.Item>
                            </Descriptions>
                        </Col>

                    </Row>
                    </TabPane>

                    <TabPane
                    tab={
                        <span>
                        <DollarOutlined />
                        평균 연봉
                        </span>
                    }
                    key="2"
                    >
                    Tab 2
                    </TabPane>
                </Tabs>
            </Card>
            </Col>
        </Row>
    );
};

export default Ranking;
