import React, { useState } from 'react';
import moment from 'moment';
import { Card, Col, Row, Modal, Button, Comment, List, Input } from 'antd';
import { EditOutlined,UserOutlined } from '@ant-design/icons';
import { FcCollaboration } from "react-icons/fc";
import '../speach/Speach.scss';




const Speach = () => {
    const { TextArea } = Input;

    const data = [
      {
        author: '익명이당',
        avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
        content: (
          <p>
            여기 초봉 이정도로 준다고?
          </p>
        ),
        datetime: (
          <span>{moment().subtract(1, 'days').fromNow()}</span>
        ),
      },
      {
        author: '역기다가',
        avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
        content: (
          <p>
            공고 떴음 지원 해보셈! 여기 현직자가 엄청 좋다고 말했습니다!
          </p>
        ),
        datetime: (
          <span>{moment().subtract(2, 'days').fromNow()}</span>
        ),
      },
    ];

    const [modalVisible, setModalVisible] = useState(false);

    const modal = (value) => {
      setModalVisible(value)
    } 

    const onChange = e => {
      console.log('Change:', e.target.value);
    };

    return (
        <Row className="card_section">
            <Col xs={{span: 24}} sm={{ span: 14, offset: 5 }}>
                <Card 
                  title={
                    <span>
                        <div className='title_logo'><FcCollaboration/></div> 
                        <div className='title_content'>의견</div>
                    </span>
                  } 
                  style={{ width: "100%" }}
                >

                <Row className='replies_write'>
                  <Col span={12}>
                    {`${data.length} replies`}
                  </Col>
                  <Col span={12}>
                    <Button size="small" type="primary" onClick={() => modal(true)}>
                      <EditOutlined/> 글쓰기
                    </Button>
                  </Col>
                </Row>

                <Modal
                  title="한국전력공사"
                  centered
                  visible={modalVisible}
                  onOk={() => modal(false)}
                  onCancel={() => modal(false)}
                  width={1000}
                  okText={'게시'}
                  cancelText={'취소'}
                >
                  <p>
                    <TextArea  
                      placeholder="내용을 입력해주세요 :)"
                      autoSize={{ minRows: 6, maxRows: 10 }}
                      showCount maxLength={500} 
                      onChange={onChange} 
                    />
                  </p>
                  <p>
                    <Input prefix={<UserOutlined />}/>
                  </p>
                </Modal>
                <List
                  className="comment-list"
                  // header={`${data.length} replies`}
                  itemLayout="horizontal"
                  dataSource={data}
                  renderItem={item => (
                    <li>
                      <Comment
                        actions={item.actions}
                        author={item.author}
                        avatar={item.avatar}
                        content={item.content}
                        datetime={item.datetime}
                      />
                    </li>
                  )}
                />
                </Card>
            </Col>
        </Row>
    );
};

export default Speach;
