import React, { useEffect } from 'react';
import useAxios from 'axios-hooks'
// import Axios from 'axios';
import { Card, Col, Row, Table, Tabs, Badge} from 'antd';
import { MoneyCollectOutlined, DollarOutlined } from '@ant-design/icons';
import { FcTodoList } from "react-icons/fc";
import './TableList.scss';





const TableList = () => {
    const { TabPane } = Tabs;

    const rankColumn = [
      { title: '순위', dataIndex: 'rank', key: '1' },
    ]

    const rankData = [
      {
          key: '1',
          rank: '1'
      },
      {
        key: '2',
        rank: '2'
      }
    ]

    const columns = [
        //  { title: '순위', width: 60, dataIndex: 'rank', key: '1' },
         { title: '기업', width: 215, dataIndex: 'name', key: '2'},
         { title: '기본급', dataIndex: 'nomal', key: '3', sorter: {compare: (a, b) => a.nomal - b.nomal} },
         { title: '고정수당', dataIndex: 'fixed', key: '4', sorter: {compare: (a, b) => a.fixed - b.fixed} },
         { title: '실적수당', dataIndex: 'performance', key: '5', sorter: {compare: (a, b) => a.performance - b.performance} },
         { title: '복리후생비', dataIndex: 'benefit', key: '6', sorter: {compare: (a, b) => a.benefit - b.benefit} },
         { title: '성과 상여금', dataIndex: 'person_bonus', key: '7', sorter: {compare: (a, b) => a.person_bonus - b.person_bonus} },
         { title: '경평 성과급', dataIndex: 'company_bonus', key: '8', sorter: {compare: (a, b) => a.company_bonus - b.company_bonus} },
         { title: '기타', dataIndex: 'etc', key: '9', sorter: {compare: (a, b) => a.etc - b.etc}},
         {
            title: '총 연봉',
            key: 'operation',
            fixed: 'right',
            width: 100,
            dataIndex: 'total',
            sorter: {compare: (a, b) => a.total - b.total,multiple: 1},
          },
      ];

      const [{ data, loading, error }] = useAxios(
        'http://localhost:8000/api/first_money/'
      )

      const dataRank = {}
      data && data.forEach((data, index) => {
        let temp = {}

        temp['key'] = index + 1
        temp['rank'] = index + 1

        dataRank.push(temp)
      });

      // companyAllFirst.forEach((firstList, index) => {   
      //   let temp = {};

      //   temp['rank'] = index+1;
      //   temp['name'] = firstList.name;
      //   temp['base'] = firstList.base.toLocaleString('en');;
      //   temp['fixed'] = firstList.fixed.toLocaleString('en');;
      //   temp['performance'] = firstList.performance.toLocaleString('en');;
      //   temp['welfare'] = firstList.welfare.toLocaleString('en');;
      //   temp['bonus'] = firstList.bonus.toLocaleString('en');;
      //   temp['manage'] = firstList.manage.toLocaleString('en');
      //   temp['total'] = firstList.total

      //   data.push(temp);
      // });

      console.log(data.length)



    return (
        <Row className="card_section">
            <Col xs={{span: 24}} sm={{ span: 14, offset: 5 }}>
                <Card 
                  title={
                    <span>
                        <div className='title_logo'><FcTodoList/></div> 
                        <div className='title_content'>전체</div>
                    </span>
                  } 
                  style={{ width: "100%" }}
                >
                <Tabs defaultActiveKey="1">
                  <TabPane
                    tab={
                      <span>
                        <MoneyCollectOutlined />
                        신입 초봉
                      </span>
                    }
                    key="1"
                  >
                    <Row>
                        <Col span={24}>
                            <div className="choice">
                                <Badge status="processing"  text="전체"/>
                                <Badge status="default"  text="소재지"/>
                                <Badge status="default"  text="주무부처"/>
                                <Badge status="default"  text="기관유형"/>
                            </div>
                        </Col>
                    </Row>

                    <Row className="table_wrapper">
                      <Col xs={{span: 5}} sm={{ span: 2 }}>
                       <Table 
                        bordered={false}
                        columns={rankColumn} 
                        dataSource={rankData} 
                        pagination={{hideOnSinglePage: true}} 
                        />
                      </Col>
                      <Col xs={{span: 19}} sm={{ span: 22}}>
                        <Table 
                        columns={columns} 
                        dataSource={data} 
                        scroll={{ x: 1200 }}
                        pagination={{hideOnSinglePage: true}} 
                        />
                      </Col>
                    </Row>

                  </TabPane>
                  <TabPane
                    tab={
                      <span>
                        <DollarOutlined />
                        평균 연봉
                      </span>
                    }
                    key="2"
                  >
                    Tab 2
                  </TabPane>
                </Tabs>    


                </Card>
            </Col>
        </Row>
    );
};

export default TableList;
