import React, { useEffect, useState } from 'react';
import moment from 'moment';
import { Card, Col, Row, Modal, Button, Comment, List, Input, Form } from 'antd';
import { EditOutlined,UserOutlined, KeyOutlined } from '@ant-design/icons';
import { FcCollaboration } from "react-icons/fc";
import Axios from 'axios';
import '../speach/Speach.scss';




const Speach = ({uniq_num}) => {
    const { TextArea } = Input;

    const commentUrl = `http://localhost:8000/api/comment/${uniq_num}`;

    const [comment, setComment] = useState()
    useEffect(() => {
      Axios.get(commentUrl)
        .then(response => {
          const {data} = response;
          setComment(data);
        })
        .catch(error => {
          console.log(error)
        })
    }, [])


    const data = [];

    comment && comment.forEach((comment, index) => {   
      let temp = {};

      temp['author'] = comment.username;
      temp['avatar'] = 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png';
      temp['content'] = comment.content;
      temp['datetime'] = moment().subtract(3, 'days').fromNow();

      data.push(temp);
    });

    const [modalVisible, setModalVisible] = useState(false);

    const modal = (value) => {
      setModalVisible(value)
    } 

    const onChange = e => {
      console.log('Change:', e.target.value);
    };

    return (
        <Row className="card_section">
            <Col xs={{span: 24}} sm={{ span: 14, offset: 5 }}>
                <Card 
                  title={
                    <span>
                        <div className='title_logo'><FcCollaboration/></div> 
                        <div className='title_content'>의견</div>
                    </span>
                  } 
                  style={{ width: "100%" }}
                >

                <Row className='replies_write'>
                  <Col span={12}>
                    {`${data.length} replies`}
                  </Col>
                  <Col span={12}>
                    <Button size="small" type="primary" onClick={() => modal(true)}>
                      <EditOutlined/> 글쓰기
                    </Button>
                  </Col>
                </Row>

                <Modal
                  title="한국전력공사"
                  centered
                  visible={modalVisible}
                  onOk={() => modal(false)}
                  onCancel={() => modal(false)}
                  width={1000}
                  okText={'게시'}
                  cancelText={'취소'}
                >
                  <Form>
                  <div>
                  <Row>
                    <Col sm={{ span: 6 }}>    
                      <Form.Item
                        name="username"
                        rules={[{ required: true, message: 'Please input your username!' }]}
                      >
                        <Input size="large" prefix={<UserOutlined/>} placeholder="* 닉네임(3글자-10글자)" maxLength={10}/>
                      </Form.Item>

                    </Col>
                    <Col sm={{ span: 6, offset: 1 }}>
                      <Form.Item
                        name="password"
                        rules={[{ required: true, message: 'Please input your username!' }]}
                      >
                        <Input.Password size="large" prefix={<KeyOutlined/>} placeholder="* 비밀번호(4자리)" maxLength={4}/>
                      </Form.Item>
                    </Col>
                  </Row>
                  </div>
                  <div>
                  <Form.Item
                        name="content"
                        rules={[{ required: true, message: 'Please input your username!' }]}
                  >
                    <TextArea  
                      placeholder="내용을 입력해주세요 :)"
                      autoSize={{ minRows: 6, maxRows: 10 }}
                      showCount maxLength={500} 
                      onChange={onChange} 
                    />
                  </Form.Item>  
                  </div>

                  <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
        <Button type="primary" htmlType="submit">
          Submit
        </Button>
      </Form.Item>

                  </Form>
                </Modal>
                <List
                  className="comment-list"
                  // header={`${data.length} replies`}
                  itemLayout="horizontal"
                  dataSource={data}
                  renderItem={item => (
                    <li>
                      <Comment
                        actions={item.actions}
                        author={item.author}
                        avatar={item.avatar}
                        content={item.content}
                        datetime={item.datetime}
                      />
                    </li>
                  )}
                />
                </Card>
            </Col>
        </Row>
    );
};

export default Speach;
