import React from 'react';
import useAxios from 'axios-hooks'
import { Card, Col, Row, Table, Tabs} from 'antd';
import { MoneyCollectOutlined, DollarOutlined } from '@ant-design/icons';
import { FcTodoList } from "react-icons/fc";
import { Input, Button, Space } from 'antd';
import Highlighter from 'react-highlight-words';
import { SearchOutlined } from '@ant-design/icons';



const TableList = () => {
    const { TabPane } = Tabs;
    state = {
      searchText: '',
      searchedColumn: '',
    };


    handleSearch = (selectedKeys, confirm, dataIndex) => {
      confirm();
      this.setState({
        searchText: selectedKeys[0],
        searchedColumn: dataIndex,
      });
    };

    handleReset = clearFilters => {
      clearFilters();
      this.setState({ searchText: '' });
    };
    getColumnSearchProps = dataIndex => ({
      filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
        <div style={{ padding: 8 }}>
          <Input
            ref={node => {
              this.searchInput = node;
            }}
            placeholder={`Search ${dataIndex}`}
            value={selectedKeys[0]}
            onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
            onPressEnter={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
            style={{ marginBottom: 8, display: 'block' }}
          />
          <Space>
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
              icon={<SearchOutlined />}
              size="small"
              style={{ width: 90 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
            <Button
              type="link"
              size="small"
              onClick={() => {
                confirm({ closeDropdown: false });
                this.setState({
                  searchText: selectedKeys[0],
                  searchedColumn: dataIndex,
                });
              }}
            >
              Filter
            </Button>
          </Space>
        </div>
      ),
      filterIcon: filtered => <SearchOutlined style={{ color: filtered ? '#1890ff' : undefined }} />,
      onFilter: (value, record) =>
        record[dataIndex]
          ? record[dataIndex].toString().toLowerCase().includes(value.toLowerCase())
          : '',
      onFilterDropdownVisibleChange: visible => {
        if (visible) {
          setTimeout(() => this.searchInput.select(), 100);
        }
      },
      render: text =>
        this.state.searchedColumn === dataIndex ? (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.state.searchText]}
            autoEscape
            textToHighlight={text ? text.toString() : ''}
          />
        ) : (
          text
        ),
    });



    const columns = [
        //  { title: '순위', width: 60, dataIndex: 'rank', key: '1' },
         { title: '기업', width: 215, dataIndex: 'name', key: '2', ...this.getColumnSearchProps('name')},
         { title: '기본급', dataIndex: 'nomal', key: '3', sorter: {compare: (a, b) => a.total - b.total} },
         { title: '고정수당', dataIndex: 'fixed', key: '4', sorter: {compare: (a, b) => a.total - b.total} },
         { title: '실적수당', dataIndex: 'performance', key: '5', sorter: {compare: (a, b) => a.total - b.total} },
         { title: '복리후생비', dataIndex: 'benefit', key: '6', sorter: {compare: (a, b) => a.total - b.total} },
         { title: '성과 상여금', dataIndex: 'person_bonus', key: '7', sorter: {compare: (a, b) => a.total - b.total} },
         { title: '경평 성과급', dataIndex: 'company_bonus', key: '8', sorter: {compare: (a, b) => a.total - b.total} },
         { title: '기타', dataIndex: 'etc', key: '9', sorter: {compare: (a, b) => a.total - b.total}},
         {
            title: '총 연봉',
            key: 'operation',
            fixed: 'right',
            width: 100,
            dataIndex: 'total',
            sorter: {compare: (a, b) => a.total - b.total,multiple: 1},
          },
      ];

      const [{ data, loading, error }] = useAxios(
        'http://localhost:8000/api/first_money/'
      )

      const rankColumn = [
        { title: '순위', dataIndex: 'rank', key: '1' },
      ]

      const rankData = [
        {
            key: '1',
            rank: '1'
        },
        {
          key: '2',
          rank: '2'
        }
      ]

    return (
        <Row className="card_section">
            <Col xs={{span: 24}} sm={{ span: 14, offset: 5 }}>
                <Card 
                  title={
                    <span>
                        <div className='title_logo'><FcTodoList/></div> 
                        <div className='title_content'>전체</div>
                    </span>
                  } 
                  style={{ width: "100%" }}
                >
                <Tabs defaultActiveKey="1">
                  <TabPane
                    tab={
                      <span>
                        <MoneyCollectOutlined />
                        신입 초봉
                      </span>
                    }
                    key="1"
                  >
                    <Row>
                      <Col xs={{span: 5}} sm={{ span: 2 }}>
                       <Table 
                        bordered={false}
                        columns={rankColumn} 
                        dataSource={rankData} 
                        pagination={{hideOnSinglePage: true}} 
                        />
                      </Col>
                      <Col xs={{span: 19}} sm={{ span: 22}}>
                        <Table 
                        columns={columns} 
                        dataSource={data} 
                        scroll={{ x: 1200 }}
                        pagination={{hideOnSinglePage: true}} 
                        />
                      </Col>
                    </Row>

                  </TabPane>
                  <TabPane
                    tab={
                      <span>
                        <DollarOutlined />
                        평균 연봉
                      </span>
                    }
                    key="2"
                  >
                    Tab 2
                  </TabPane>
                </Tabs>    


                </Card>
            </Col>
        </Row>
    );
};

export default TableList;
