import React, { createContext, useEffect, useState } from 'react';
import {Route, Switch} from 'react-router-dom'
import IndexPage from './pages/IndexPage';
import RankPage from './pages/RankPage';
import SignInPage from './pages/accounts/SignInPage';
import SignUpPage from './pages/accounts/SignUpPage';
import useLocalStorage from './utils/useLocalStorage';
import Axios from 'axios';

const JwtContext = createContext();
const LoginContext = createContext();

const App = () => {
  const [jwtToken, setJwtToken] = useLocalStorage("jwtToken", "");

  const [loginCheck, setLoginCheck] = useState(false);

    useEffect(() => {
        if(jwtToken.length > 0){
            setLoginCheck(true)
        }else{
            setLoginCheck(false)
        }
    }, [jwtToken, loginCheck])



  return (
    <JwtContext.Provider value={jwtToken}>
      <LoginContext.Provider>
        <Switch>
          <Route path='/rank/:uniq_num' component={RankPage}/>
          <Route path='/accounts/sign_in' component={SignInPage}/>
          <Route path='/accounts/sign_up' component={SignUpPage}/>
          <Route path='/' component={IndexPage}/>
        </Switch>
      </LoginContext.Provider>
    </JwtContext.Provider>
  );
};

export {LoginContext}
export {JwtContext};
export default App;
