import React from 'react';
import { Card, Col, Row, Table, Tabs} from 'antd';
import { MoneyCollectOutlined, DollarOutlined } from '@ant-design/icons';
import { FcTodoList } from "react-icons/fc";



const TableList = () => {
    const { TabPane } = Tabs;

    const columns = [
        {
            title: '순위',
            width: 60,
            dataIndex: 'rank',
            key: 'rank',
          },
          {
            title: '기업',
            width: 215,
            dataIndex: 'name',
            key: 'name',
          },
         { title: '기본급', dataIndex: 'nomal', key: '1' },
         { title: '고정수당', dataIndex: 'fixed', key: '2' },
         { title: '실적수당', dataIndex: 'performance', key: '3' },
         { title: '복리후생비', dataIndex: 'benefit', key: '4' },
         { title: '성과 상여금', dataIndex: 'person_bonus', key: '5' },
         { title: '경평 성과급', dataIndex: 'company_bonus', key: '6' },
         { title: '기타', dataIndex: 'etc', key: '7' },
         {
            title: '총 연봉',
            key: 'operation',
            fixed: 'right',
            width: 80,
            dataIndex: 'total',
            sorter: {
                compare: (a, b) => a.total - b.total,
                multiple: 1,
            },
          },
      ];

      const data = [
        {
            key: '1',
            rank: '1',
            name: '한국해외인프라도시개발지원공사',
            nomal: '32,000',
            fixed: '0',
            performance: '10,000',
            benefit: '32,110',
            person_bonus: '1,000',
            company_bonus: '11,000',
            etc: '0',
            total: 52000
          },
          {
            key: '2',
            rank: '2',
            name: '한국수자원공사',
            nomal: '32,000',
            fixed: '0',
            performance: '10,000',
            benefit: '32,110',
            person_bonus: '1,000',
            company_bonus: '12,000',
            etc: '0',
            total: 53000
          },
      ];

    return (
        <Row className="card_section">
            <Col xs={{span: 24}} sm={{ span: 14, offset: 5 }}>
                <Card 
                  title={
                    <span>
                        <div className='title_logo'><FcTodoList/></div> 
                        <div className='title_content'>전체</div>
                    </span>
                  } 
                  style={{ width: "100%" }}
                >
                <Tabs defaultActiveKey="1">
                  <TabPane
                    tab={
                      <span>
                        <MoneyCollectOutlined />
                        신입 초봉
                      </span>
                    }
                    key="1"
                  >
                    <Table 
                      columns={columns} 
                      dataSource={data} 
                      scroll={{ x: 1200 }}
                      pagination={{hideOnSinglePage: true}} 
                    />
                  </TabPane>
                  <TabPane
                    tab={
                      <span>
                        <DollarOutlined />
                        평균 연봉
                      </span>
                    }
                    key="2"
                  >
                    Tab 2
                  </TabPane>
                </Tabs>    


                </Card>
            </Col>
        </Row>
    );
};

export default TableList;
