import React, { useEffect, useState } from 'react';
import moment from 'moment';
import { Card, Col, Row, Modal, Button, Comment, List, Input, Form, notification } from 'antd';
import { EditOutlined,UserOutlined, KeyOutlined, SmileOutlined } from '@ant-design/icons';
import { FcCollaboration } from "react-icons/fc";
import Axios from 'axios';
import '../speach/Speach.scss';




const Speach = ({uniq_num}) => {
    const { TextArea } = Input;

    const commentUrl = `http://localhost:8000/api/comment/${uniq_num}`;

    const [comment, setComment] = useState([])
    useEffect(() => {
      Axios.get(commentUrl)
        .then(response => {
          const {data} = response;
          setComment(data);
        })
        .catch(error => {
          console.log(error)
        })
    }, [])


    // const data = [];
    // const [data, setData] = useState([]);
    // let pre_data = [];

    // comment && comment.forEach((comment, index) => {   
    //   let temp = {};

    //   temp['author'] = comment.username;
    //   temp['avatar'] = 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png';
    //   temp['content'] = comment.content;
    //   temp['datetime'] = moment().subtract(3, 'days').fromNow();

    //   pre_data.push(temp);
    // });
    // setData(pre_data)

    const [modalVisible, setModalVisible] = useState(false);

    const modal = (value) => {
      setModalVisible(value)
    } 

    const onFinish = (values) => {
      const {company, username, password, content} = values;
      const data = {company, username, password, content}

      Axios.post("http://localhost:8000/api/comment/", data)
        .then(response => {
            notification.open({
              message: '작성 완료',
              icon: <SmileOutlined style={{color: '#108ee9'}}/>
            })

            modal(false)

            setComment([
              ...comment,
              response.data
            ])
        })
        .catch(error => {
          console.log(error.response)
        })


    };


    const [commentModal, setCommentModal] = useState(false)
    const [commentId, setCommentId] = useState()
    const handleRemove = (id) => {
      // const deleteUrl = `http://localhost:8000/api/comment/delete/${id}`;
      // Axios.delete(deleteUrl)
      // setComment(comment.filter(comment => comment.id !== id))
      setCommentModal(true)
      setCommentId(id)
      console.log(commentId)
    }

    const checkPassword = (id) => {
      console.log("???", id)
    }

    return (
        <Row className="card_section">
            <Col xs={{span: 24}} sm={{ span: 14, offset: 5 }}>
                <Card 
                  title={
                    <span>
                        <div className='title_logo'><FcCollaboration/></div> 
                        <div className='title_content'>의견</div>
                    </span>
                  } 
                  style={{ width: "100%" }}
                >

                <Row className='replies_write'>
                  <Col span={12}>
                    {`${comment.length} replies`}
                  </Col>
                  <Col span={12}>
                    <Button size="small" type="primary" onClick={() => modal(true)}>
                      <EditOutlined/> 글쓰기
                    </Button>
                  </Col>
                </Row>

                <Modal
                  title="한국전력공사"
                  centered
                  visible={modalVisible}
                  onOk={() => modal(false)}
                  onCancel={() => modal(false)}
                  width={1000}
                  okText={'게시'}
                  cancelText={'취소'}
                >
                  <Form onFinish={onFinish}>
                  <div>
                  <Row>
                    <Col sm={{ span: 6 }}>    
                    <Form.Item
                        name="company"
                        hidden={true}
                        initialValue={uniq_num}
                      >
                        <Input/>
                      </Form.Item>
                      <Form.Item
                        name="username"
                        rules={[
                          { required: true, message: '닉네임을 입력해주세요' },
                          { min: 3, message: '3글자 이상 입력해 주세요 :D' },
                          { max: 10, message: '최대 10글자까지 입력할 수 있어요 :D' }
                        ]}
                      >
                        <Input size="large" prefix={<UserOutlined/>} placeholder="* 닉네임(3글자-10글자)" maxLength={10}/>
                      </Form.Item>

                    </Col>
                    <Col sm={{ span: 6, offset: 1 }}>
                      <Form.Item
                        name="password"
                        rules={[
                          { required: true, message: '비밀번호를 입력해 주세요 :D' },
                          { min: 4, message: '4자를 입력해 주세요 :D' },
                          { max: 4, message: '4자를 입력해 주세요 :D' }
                        ]}
                      >
                        <Input.Password size="large" prefix={<KeyOutlined/>} placeholder="* 비밀번호(4자리)" maxLength={4}/>
                      </Form.Item>
                    </Col>
                  </Row>
                  </div>
                  <div>
                  <Form.Item
                        name="content"
                        rules={[
                          { required: true, message: '내용을 입력해 주세요 :D' },
                          { min: 3, message: '3글자 이상 입력해 주세요 :D' },
                          { max: 500, message: '최대 500글자까지 입력할 수 있어요 :D' }
                        ]}
                  >
                    <TextArea  
                      placeholder="내용을 입력해주세요 :)"
                      autoSize={{ minRows: 6, maxRows: 10 }}
                      showCount maxLength={500} 
                    />
                  </Form.Item>  
                  </div>
                  <hr className="modal-hr"/>
                  <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
                    <Button type="primary" htmlType="submit">
                      게시
                    </Button>
                  </Form.Item>

                  </Form>
                </Modal>
                <List
                  className="comment-list"
                  itemLayout="horizontal"
                  dataSource={comment}
                  renderItem={(comment) => (
                    <li id={comment.id}>
                      <Comment
                        // actions={item.actions}
                        actions={[<span onClick={() => handleRemove(comment.id)}>삭제</span>]}
                        author={comment.username}
                        avatar= 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png'
                        content={comment.content}
                        datetime={
                          moment(comment.created_at.split('.')[0],  'YYYY.MM.DD.HH.mm.ss ').fromNow()
                        }
                      />


                    </li>
                  )}
                />

                <Modal
                  title="비밀번호(4자리)"
                  centered
                  visible={commentModal}
                  onOk={() => checkPassword(comment.id)}
                  onCancel={() => setCommentModal(false)}
                >
                  <Form
                  name="comment_delete"
                  initialValues={{ remember: true }}
                  // onFinish={onFinish}
                  // onFinishFailed={onFinishFailed}
                 >
                    <Form.Item
                      name="comment_password"
                      rules={[
                          { required: true, message: '비밀번호를 입력해 주세요 :D' },
                          { min: 4, message: '4자를 입력해 주세요 :D' },
                          { max: 4, message: '4자를 입력해 주세요 :D' }
                      ]}
                    >
                      <Input.Password maxLength={4}/>
                    </Form.Item>
                  </Form>
                </Modal>

                </Card>
            </Col>
        </Row>
    );
};

export default Speach;
