import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import { Descriptions, Row, Col } from 'antd';
import { Avatar } from 'antd';
import { UserOutlined } from '@ant-design/icons';
import './Info.scss';


const Info = ({uniq_num}) => {
    const apiUrl = `http://localhost:8000/api/first_money/${uniq_num}`

    const [name, setName] = useState('')
    const [location, setLocation] = useState('')
    const [img, setImg] = useState('')
    const [authority, setAuthority] = useState('')
    const [type, setType] = useState('')
    const [purpose, setPurpose] = useState('')
    const [setting, setSetting] = useState('')

    useEffect(() => {
        Axios.get(apiUrl)
            .then(response => {
                const {data} = response;

                setName(data.name)
                setLocation(data.name)
                setImg(data.name)
                setAuthority(data.name)
                setType(data.name)
                setPurpose(data.name)
                setSetting(data.name)
            })
            .catch(error => {
                console.log(error.response);
            })
    }, [apiUrl])


    return (
        <Row className="info_wrapper">
            <Col xs={{span: 24}} sm={{ span: 14, offset: 5 }}>
                <Avatar shape="square" size={100} icon={<UserOutlined />} />

                <Descriptions title={name} size='small' bordered>
                    <Descriptions.Item label="신입초봉" span={2}>41,035 천원</Descriptions.Item>
                    <Descriptions.Item label="평균연봉" span={2}>86,660 천원</Descriptions.Item>
                    <Descriptions.Item label="위치">서울특별시</Descriptions.Item>
                    <Descriptions.Item label="주무기관">과학기술정보통신부</Descriptions.Item>
                    <Descriptions.Item label="기관유형">기타공공기관</Descriptions.Item>

                    <Descriptions.Item label="설립목적" span={4}>
                    우체국금융 업무를 효과적으로 지원함으로써 우체국예금보험사업의 향상·발전에 기여
                    </Descriptions.Item>
                    <Descriptions.Item label="주요기능">
                        ㅇ 국내외 금융시장에 대한 조사·연구<br/>
                        ㅇ 우체국금융 신서비스 도입 및 마케팅 방안 연구<br/>
                        ㅇ 우체국예금·보험 상품 개발<br/>
                        ㅇ 우체국보험 청약·지급심사 및 사고조사<br/>
                        ㅇ 우체국보험관련 교육 및 우체국금융 고객관리·마케팅 지원<br/>
                        ㅇ 금융시장 리서치 및 신용분석 지원<br/>
                        ㅇ 우체국보험기금 재산의 수탁관리<br/>
                        ㅇ 우체국금융 고객센터 운영수탁
                    </Descriptions.Item>
                </Descriptions>
            </Col>
      </Row>
    );
};

export default Info;
