import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import { Descriptions, Row, Col } from 'antd';
import './Info.scss';


const Info = ({uniq_num}) => {
    const firstMoneyUrl = `http://localhost:8000/api/first_money/${uniq_num}`
    const aveMoneyUrl = `http://localhost:8000/api/ave_money/${uniq_num}`

    const [firstTotal, setFirstTotal] = useState('')
    const [aveTotal, setAVeTotal] = useState('')

    const [name, setName] = useState('')
    const [location, setLocation] = useState('')
    const [img, setImg] = useState('')
    const [authority, setAuthority] = useState('')
    const [type, setType] = useState('')
    const [purpose, setPurpose] = useState('')
    const [setting, setSetting] = useState('')

    useEffect(() => {
        Axios.get(firstMoneyUrl)
            .then(response => {
                const {data} = response;

                setName(data.name)
                setLocation(data.company.location)
                setImg(data.company.img_url)
                setAuthority(data.company.authority)
                setType(data.company.type)
                setPurpose(data.company.purpose)
                setSetting(data.company.function)

                setFirstTotal(data.total)
            })
            .catch(error => {
                // console.log(error.response);
            })

            Axios.get(aveMoneyUrl)
                .then(response => {
                    const {data} = response;
                    setAVeTotal(data.total)
                })
                .catch(error => {
                    // console.log(error.response);
            })

    }, [firstMoneyUrl, aveMoneyUrl])


    function moneyToUnit(money) {
        return money.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ',');
    }    


    return (
        <Row className="info_wrapper">
            <Col xs={{span: 24}} sm={{ span: 14, offset: 5 }}>
                {/* <Image width={200} src={img} preview={false}/> */}

                <Descriptions title={name} size='small' bordered>
                    <Descriptions.Item label="신입초봉" span={2}>{moneyToUnit(firstTotal)} 천원</Descriptions.Item>
                    <Descriptions.Item label="평균연봉" span={2}>{moneyToUnit(aveTotal)} 천원</Descriptions.Item>
                    <Descriptions.Item label="위치">{location}</Descriptions.Item>
                    <Descriptions.Item label="주무기관">{authority}</Descriptions.Item>
                    <Descriptions.Item label="기관유형">{type}</Descriptions.Item>

                    <Descriptions.Item label="설립목적" span={4}>
                        {purpose}
                    </Descriptions.Item>
                    <Descriptions.Item label="주요기능">
                        {setting}
                    </Descriptions.Item>
                </Descriptions>
            </Col>
      </Row>
    );
};

export default Info;
